sect1.example:
   Exemplu de clasă abstractă


<?php
abstract class AbstractClass
{
    // Forțează implementarea în copii a următoarelor metode
    abstract protected function getValue();
    abstract protected function prefixValue($prefix);

    // Metodă comună
    public function printOut() {
        print $this->getValue() . "\n";
    }
}

class ConcreteClass1 extends AbstractClass
{
    protected function getValue() {
        return "ConcreteClass1";
    }

    public function prefixValue($prefix) {
        return "{$prefix}ConcreteClass1";
    }
}

class ConcreteClass2 extends AbstractClass
{
    public function getValue() {
        return "ConcreteClass2";
    }

    public function prefixValue($prefix) {
        return "{$prefix}ConcreteClass2";
    }
}

$class1 = new ConcreteClass1;
$class1->printOut();
echo $class1->prefixValue('FOO_') ."\n";

$class2 = new ConcreteClass2;
$class2->printOut();
echo $class2->prefixValue('FOO_') ."\n";
?>


   &example.outputs;


ConcreteClass1
FOO_ConcreteClass1
ConcreteClass2
FOO_ConcreteClass2

   Exemplu cu clasă abstractă


<?php
abstract class AbstractClass
{
    // Metoda noastră abstractă trebuie doar să definească argumentele necesare
    abstract protected function prefixName($name);

}

class ConcreteClass extends AbstractClass
{

    // Clasa noastră fiică poate defini argumente opționale care nu sunt prezente în semnătura părintelui
    public function prefixName($name, $separator = ".") {
        if ($name == "Pacman") {
            $prefix = "Mr";
        } elseif ($name == "Pacwoman") {
            $prefix = "Ms";
        } else {
            $prefix = "";
        }
        return "{$prefix}{$separator} {$name}";
    }
}

$class = new ConcreteClass;
echo $class->prefixName("Pacman"), "\n";
echo $class->prefixName("Pacwoman"), "\n";
?>


   &example.outputs;


Mr. Pacman
Ms. Pacwoman
