refentry.refsect1:
&reftitle.description;

 boolncurses_getmouse
 arraymevent

&warn.experimental.func;


 ncurses_getmouse citește evenimentul șoricelului din rândul de evenimente.

&reftitle.returnvalues;


 Întoarce &false; dacă un eveniment de la șoricel este într-adevăr vizibil în
 fereastra dată, &true; în caz contrar.

  &reftitle.examples;



    Exemplu ncurses_getmouse


<?php
switch (ncurses_getch()){
  case NCURSES_KEY_MOUSE:
    if (!ncurses_getmouse($mevent)){
      if ($mevent["mmask"] & NCURSES_MOUSE_BUTTON1_PRESSED){
        $mouse_x = $mevent["x"]; // Save mouse position
        $mouse_y = $mevent["y"];
      }
    }
  break;

  default:
    /* .... */
}
?>
