refsect1.para:
     Variabilele de tip scalar includ tipurile integer, 
     float, string ºi boolean.  
     Tipurile array, object ºi resource 
     nu sunt tipuri scalare.




<?php
function show_var($var) {
    if (is_scalar($var)) {
        echo $var;
    } else {
        var_dump($var);
    }
}
$pi = 3.1416;
$proteins = array("hemoglobin", "cytochrome c oxidase", "ferredoxin");

show_var($pi);
// afiºeazã 3.1416

show_var($proteins);
// afiºeazã:
// array(3) {
//   [0]=>
//   string(10) "hemoglobin"
//   [1]=>
//   string(20) "cytochrome c oxidase"
//   [2]=>
//   string(10) "ferredoxin"
// }
?>
