refentry.refsect1:
&reftitle.returnvalues;


 Întoarce un string conţinând reprezentarea value sub
 formă de byte-stream care poate fi stocată oriunde.

  &reftitle.examples;



    Exemplu serialize


<?php
// $session_data conţine un tablou multi-dimensional cu informaţie despre
// sesiune pentru utilizatorul curent. Vom utiliza serialize() pentru a
// stoca această informaţie într-o bază de date după încheierea interpelării.

$conn = odbc_connect("webdb", "php", "chicken");
$stmt = odbc_prepare($conn,
      "UPDATE sessions SET data = ? WHERE id = ?");
$sqldata = array (serialize($session_data), $_SERVER['PHP_AUTH_USER']);
if (!odbc_execute($stmt, $sqldata)) {
    $stmt = odbc_prepare($conn,
     "INSERT INTO sessions (id, data) VALUES(?, ?)");
    if (!odbc_execute($stmt, $sqldata)) {
        /* Ceva nu este în regulă.. */
    }
}
?>

